import React from 'react';
import styles from "../Styles/StatusPopup.module.css";

const StatusPopup = ({ isVisible, statusMessages, onClose }) => {
    if (!isVisible) return null;

    return (
        <div className={styles.statusPopupOverlay}>
            <div className={styles.statusPopupPopup}>
                <button className={styles.statusPopupCloseButton} onClick={onClose}>âœ•</button>
                <h2>Status</h2>
                <ul>
                    {statusMessages.map((msg, index) => (
                        <li key={index} className={styles[`${msg.type}-message`]}>
                            <span className={styles.tick}></span> {msg.text}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default StatusPopup;
