import React, { useState } from 'react';
import Sidebar from '../Components/sidebar';
import styles from  '../Styles/DeploymentOptions.css';

const DeploymentOptions = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

    return (
      <Sidebar>
        <h1 className={styles.title}>Deployment Options ðŸš€</h1>
        <div className={styles.deploymentOptionsContainer}>
          <div className={styles.deploymentOptions}>
            <button
              className={selectedOption === 'Standalone Cloud Setup' ? styles.active : ''}
              onClick={() => handleOptionClick('Standalone Cloud Setup')}
            >
              Standalone Cloud Setup
            </button>
            <button
              className={selectedOption === 'Distributed Cloud Setup' ? styles.active : ''}
              onClick={() => handleOptionClick('Distributed Cloud Setup')}
            >
              Distributed Cloud Setup
            </button>
          </div>
          <button className={styles.nextButton} disabled={!selectedOption}>
            Next
          </button>
        </div>
      </Sidebar>
    );
  };

  export default DeepOption;
