import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Sidebar from '../Components/sidebar';
import Footer from '../Components/footer';
import '../Styles/DesignatedNode.module.css';

const DesignatedNodes = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const [selectedRows, setSelectedRows] = useState(location.state.selectedNodes || []);
    const [roles, setRoles] = useState(selectedRows.map((node, index) => ({
        id: index + 1,
        slNo: index + 1,
        ipAddress: node.ip,
        hostname: node.hostname,
        roles: []
    })));

    const [bmcFormVisible, setBmcFormVisible] = useState(false);
    const [currentNode, setCurrentNode] = useState(null);
    const [bmcDetails, setBmcDetails] = useState({
        ip: '',
        username: '',
        password: ''
    });

    const [loading, setLoading] = useState(false);
    const [deploymentCompleted, setDeploymentCompleted] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const rowsPerPage = 5;

    const handleCheckboxChange = (event, row, role) => {
        const isChecked = event.target.checked;
        const updatedRoles = roles.map(item => {
            if (item.id === row.id) {
                const rolesSet = new Set(item.roles);
                if (isChecked) {
                    rolesSet.add(role);
                } else {
                    rolesSet.delete(role);
                }
                return { ...item, roles: Array.from(rolesSet) };
            }
            return item;
        });
        setRoles(updatedRoles);

        const updatedSelectedRows = isChecked
            ? [...selectedRows, row]
            : selectedRows.filter(selectedRow => selectedRow.id !== row.id);
        setSelectedRows(updatedSelectedRows);
    };

    const handleDeploy = (node) => {
        setCurrentNode(node);
        setBmcFormVisible(true);
    };

    const handleBmcFormSubmit = async (event) => {
        event.preventDefault();
        setLoading(true);

        // Simulate API call or actual deployment logic here
        try {
            const response = await fetch('http://localhost:8000/set_pxe_boot', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(bmcDetails)
            });

            const result = await response.json();
            console.log(result);

            // Mark deployment as completed
            setDeploymentCompleted(true);

            // Optionally reset form state
            setBmcDetails({
                ip: '',
                username: '',
                password: ''
            });

            // Close the form after successful deployment
            setBmcFormVisible(false);

            // Navigate to deployment info page
            navigate('/deploymentinfo');
        } catch (error) {
            console.error('Deployment error:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleCancel = () => {
        setBmcFormVisible(false);
        setLoading(false);
    };

    const indexOfLastRow = currentPage * rowsPerPage;
    const indexOfFirstRow = indexOfLastRow - rowsPerPage;
    const currentRows = roles.slice(indexOfFirstRow, indexOfLastRow);

    const handleNextPage = () => {
        setCurrentPage(prevPage => prevPage + 1);
    };

    const handlePreviousPage = () => {
        setCurrentPage(prevPage => prevPage - 1);
    };

    return (
        <div>
        <div style={style.headers}>
            <h1>Designated Nodes</h1>
        </div>
        <div style={style.dataContainer}>
            <div className="data-table-container">
                <table style={style.dataTable}>
                    <thead>
                        <tr style={style.tableHeader}>
                            <th style={style.tableCell}>Sl No.</th>
                            <th style={style.tableCell}>IP Address</th>
                            <th style={style.tableCell}>Hostname</th>
                            <th style={style.tableCell}>Roles</th>
                            <th style={style.tableCell}>Deploy</th>
                        </tr>
                    </thead>
                    <tbody>
                        {currentRows.map((row, index) => (
                            <tr key={index} style={style.tableRow}>
                                <td style={style.tableCell}>{row.slNo}</td>
                                <td style={style.tableCell}>{row.ipAddress}</td>
                                <td style={style.tableCell}>{row.hostname}</td>
                                <td style={{ ...style.tableCell, ...style.checkboxColumn }}>
                                    <label>
                                        <input
                                            type="checkbox"
                                            checked={row.roles.includes('Compute')}
                                            onChange={(event) => handleCheckboxChange(event, row, 'Compute')}
                                        />
                                        <span>HCI</span>
                                    </label>
                                    <br />
                                    <label>
                                        <input
                                            type="checkbox"
                                            checked={row.roles.includes('Control')}
                                            onChange={(event) => handleCheckboxChange(event, row, 'Control')}
                                        />
                                        <span>Compute&Storage</span>
                                    </label>
                                </td>
                                <td style={style.tableCell}>
                                    <button onClick={() => handleDeploy(row)}>Deploy</button>
                                    {loading && currentNode && currentNode.id === row.id && (
                                        <div style={style.loader}></div>
                                    )}
                                    {deploymentCompleted && currentNode && currentNode.id === row.id && (
                                        <div style={style.completed}>Completed</div>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div style={style.pagination}>
                    <button onClick={handlePreviousPage} disabled={currentPage === 1}>
                        Previous
                    </button>
                    <button onClick={handleNextPage} disabled={indexOfLastRow >= roles.length}>
                        Next
                    </button>
                </div>
            </div>
            <Sidebar />
        </div>

        {/* BMC Form */}
        {bmcFormVisible && (
            <div style={style.bmcForm}>
                <h2>Enter BMC Details for {currentNode.hostname}</h2>
                <form onSubmit={handleBmcFormSubmit}>
                    <label>
                        BMC IP Address:
                        <input
                            type="text"
                            value={bmcDetails.ip}
                            onChange={(e) => setBmcDetails({ ...bmcDetails, ip: e.target.value })}
                            required
                        />
                    </label>
                    <label>
                        BMC Username:
                        <input
                            type="text"
                            value={bmcDetails.username}
                            onChange={(e) => setBmcDetails({ ...bmcDetails, username: e.target.value })}
                            required
                        />
                    </label>
                    <label>
                        BMC Password:
                        <input
                            type="password"
                            value={bmcDetails.password}
                            onChange={(e) => setBmcDetails({ ...bmcDetails, password: e.target.value })}
                            required
                        />
                    </label>
                    <div>
                        <button type="submit">Submit</button>
                        <button type="button" onClick={handleCancel}>Cancel</button>
                    </div>
                </form>
            </div>
        )}

        {/* <Footer /> */}
    </div>    );
};

export default DesignatedNodes;
