// DeploymentOptions.js
import React, { useState } from 'react';
import Sidebar from '../Components/sidebar';
import '../Styles/DeploymentOptions.css';

const DeploymentOptions = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleNextClick = () => {
    // Handle next button click action here
    console.log("Next button clicked");
  };

  return (
    <div className={styles.deploymentOptionsContainer}>
      {/* Sidebar component can be added here if needed */}
      <div className={styles.title}>
        <h1>Deployment Options</h1>
      </div>
      <div className={styles.deploymentOptions}>
        <div className="option">
          <input
            type="radio"
            id="standalone"
            name="deployment"
            value="Standalone Cloud Setup"
            checked={selectedOption === "Standalone Cloud Setup"}
            onChange={handleOptionChange}
          />
          <label htmlFor="standalone">Standalone Cloud Setup</label>
        </div>
        <div className="option">
          <input
            type="radio"
            id="distributed"
            name="deployment"
            value="Distributed Cloud Setup"
            checked={selectedOption === "Distributed Cloud Setup"}
            onChange={handleOptionChange}
          />
          <label htmlFor="distributed">Distributed Cloud Setup</label>
        </div>
        {/* Add more options as needed */}
      </div>
      <button className={styles.nextButton} onClick={handleNextClick} disabled={!selectedOption}>
        Next
      </button>
    </div>
  );
};

export default DeploymentOptions;
