import React, { useState } from 'react';
import Sidebar from '../Components/sidebar';
import Footer from '../Components/footer';

import '../Styles/Datatable.css'; // Import your CSS file for styling

const DataTable = () => {
    // Example data, replace with actual data fetching logic
    const [selectedRows, setSelectedRows] = useState([]);

    const data = [
        { id: 1, slNo: 1, ipAddress: '192.168.1.1', hostname: 'example.com', deploymentSection: 'Deploy' },
        { id: 2, slNo: 2, ipAddress: '192.168.1.2', hostname: 'example.net', deploymentSection: 'Deploy' },
        // Add more data as needed
    ];

    const handleCheckboxChange = (event, row) => {
        const isChecked = event.target.checked;
        if (isChecked) {
            setSelectedRows([...selectedRows, row]);
        } else {
            setSelectedRows(selectedRows.filter(selectedRow => selectedRow.id !== row.id));
        }
    };

    const handleValidate = (rowData) => {
        // Handle validation logic here, e.g., call an API to validate
        console.log('Validating:', rowData);
        // Update state or perform further actions based on validation
    };

    const handleDeploy = () => {
        // Handle deployment logic here, e.g., call an API to initiate deployment
        console.log('Deploying:', selectedRows);
        // Update state or perform further actions based on deployment
    };

    return (
            <div className="data-table-container" style={{ textAlign: 'center' }}>
            <h1>Discovery Machines</h1>
            <div className="container" style={{ display: 'inline-block', textAlign: 'center' }}>
                <div className="data-table-container">
                    {/* <h2>Data Table</h2> */}
                    <table className="data-table">
                        <thead>
                            <tr>
                                <th>Sl No.</th>
                                <th>IP Address</th>
                                <th>Hostname</th>
                                <th>Designating<br></br>Validation</th>
                                <th>Result</th>
                                <th>Deployment Section</th>

                            </tr>
                        </thead>
                        <tbody>
                            {data.map((row, index) => (
                                <tr key={index}>
                                    <td>{row.slNo}</td>
                                    <td>{row.ipAddress}</td>
                                    <td>{row.hostname}</td>
                                    <td>
                                        <button onClick={() => handleValidate(row)}>Validate</button>
                                    </td>
                                    <td>{/* Result content */}</td> {/* Placeholder for result */}
                                    <td><input type="checkbox" onChange={(event) => handleCheckboxChange(event, row)} />{row.deploymentSection}</td>

                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                <Sidebar />
                <Footer />

                {/* Deploy button */}
                <button onClick={handleDeploy} disabled={selectedRows.length === 0}>
                    Deploy
                </button>
            </div>
        </div>
    );
};

export default DataTable;
