import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from '../Components/sidebar';
import Footer from '../Components/footer';

import '../Styles/DeploymentInfo.css';

const DeploymentInfo = () => {
    const navigate = useNavigate();
    const [selectedRow, setSelectedRow] = useState(null);
    const [roles, setRoles] = useState([]); // State to hold roles from previous page

    // Example data, replace with actual data fetching logic
    const data = [
        { id: 1, slNo: 1, ipAddress: '192.168.1.1', hostname: 'example.com' },
        { id: 2, slNo: 2, ipAddress: '192.168.1.2', hostname: 'example.net' },
        // Add more data as needed
    ];

    const handleCheckboxChange = (row) => {
        if (selectedRow && selectedRow.id === row.id) {
            setSelectedRow(null); // Unselect if already selected
        } else {
            setSelectedRow(row); // Select the row
        }
    };

    const handleValidate = (rowData) => {
        // Example: Simulate validation
        setTimeout(() => {
            const updatedData = data.map(item => {
                if (item.id === rowData.id) {
                    return { ...item, validationStatus: 'PASS' }; // Replace with actual validation result
                }
                return item;
            });
            console.log('Validation complete:', updatedData);
            // Update state or perform further actions based on validation
        }, 1000); // Simulate API delay
    };

    const handleDeploy = () => {
        // Example: Simulate deployment
        console.log('Deploying:', selectedRow);
        // Implement actual deployment logic here, e.g., call an API

        navigate('/designatednodes'); // Navigate to designated nodes page after deployment
    };

    return (
        <div className="data-table-container">
            <h1>Deployment Info</h1>
            <div className="container">
                <div className="data-table-container">
                    <table className="data-table">
                        <thead>
                            <tr>
                                <th>Sl No.</th>
                                <th>IP Address</th>
                                <th>Hostname</th>
                                <th>Roles</th> {/* New column for Roles */}
                            </tr>
                        </thead>
                        <tbody>
                            {data.map((row, index) => (
                                <tr key={index}>
                                    <td>{row.slNo}</td>
                                    <td>{row.ipAddress}</td>
                                    <td>{row.hostname}</td>
                                    <td>
                                        {/* Display roles based on state */}
                                        {roles.map((role, index) => (
                                            <span key={index}>{role}</span>
                                        ))}
                                    </td>
                                    <td className="checkbox-column">
                                        <label>
                                            <input
                                                type="checkbox"
                                                onChange={() => handleCheckboxChange(row)}
                                                checked={selectedRow && selectedRow.id === row.id}
                                            />
                                            <span>Deploy</span>
                                        </label>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                <Sidebar />
                <Footer />

                <button
                    className="button-deploy"
                    onClick={handleDeploy}
                    disabled={!selectedRow}
                >
                    Deploy
                </button>
            </div>
        </div>
    );
};

export default DeploymentInfo;
