import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Sidebar from '../Components/sidebar';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import styles from "../Styles/Validation.module.css"; // Import CSS Modules

const Validation = () => {
    const location = useLocation();
    const navigate = useNavigate();

    // Access the selectedNodes from the state passed from the previous page
    const { selectedNodes } = location.state || { selectedNodes: [] };

    const handleBack = () => {
        navigate(-1); // Navigate to the previous page
        setTimeout(() => {
            window.scrollTo({ top: 0, behavior: 'smooth' });
        }, 100);
    };

    return (
        <div>
            <div className={styles.header}>
                <button className="back-button" onClick={handleBack}>
                    <FontAwesomeIcon icon={faArrowLeft} size="2x" />
                </button>
                <center>
                    <h1>Selected Devices</h1>
                </center>
            </div>
            <div className={styles.main}>
                <div className={styles['data-table-container']}>
                    <div className={styles.container}>
                        <div>
                            <table className={styles['data-table']}>
                                <thead>
                                    <tr>
                                        <th>Sl No.</th>
                                        <th>IP Address</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {selectedNodes.length === 0 ? (
                                        <tr>
                                            <td colSpan="2" className={styles['no-device-message']}><center>No devices selected</center></td>
                                        </tr>
                                    ) : (
                                        selectedNodes.map((node, index) => (
                                            <tr key={node.ip}>
                                                <td>{index + 1}</td>
                                                <td>{node.ip}</td>
                                            </tr>
                                        ))
                                    )}
                                </tbody>
                            </table>
                        </div>
                        <Sidebar />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Validation;
