// DeploymentOptions.js
import React, { useState } from 'react';
import Sidebar from '../Components/sidebar';
import  styles from '../Styles/DeploymentOptions.css'; // Import the CSS file
import Footer from '../Components/footer';// DeploymentOptions.js

const DeploymentOptions = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleOptionClick = (option) => {
    setSelectedOption(option);
  };

  const handleNextClick = () => {
    // Handle next button click action here
    console.log("Next button clicked");
  };

  return (
    <div className={styles.container}>
      <div className={styles.mainContent}>
        <h1>Deployment Options</h1>
        <div className={styles.options}>
          <div className={styles.optionBox} onClick={() => handleOptionClick("Standalone Cloud Setup")}>
            <div className={styles.optionContent}>
              <div className={styles.optionText}>Standalone Cloud Setup</div>
            </div>
          </div>
          <div className={styles.optionBox} onClick={() => handleOptionClick("Distributed Cloud Setup")}>
            <div className={styles.optionContent}>
              <div className={styles.optionText}>Distributed Cloud Setup</div>
            </div>
          </div>
        </div>
        {selectedOption && <p>Selected Option: {selectedOption}</p>}
        <button className={styles.nextButton} onClick={handleNextClick} disabled={!selectedOption}>
          Next
        </button>
      </div>
      <Sidebar />
      <Footer />
    </div>
  );
};

export default DeploymentOptions;
