import React, { useState } from 'react';
import Sidebar from '../Components/sidebar';
import Footer from '../Components/footer';

import '../Styles/Datatable.css'; // Import your CSS file for styling

const DataTable = () => {
    const [selectedRows, setSelectedRows] = useState([]);

    const data = [
        { id: 1, slNo: 1, ipAddress: '192.168.1.1', hostname: 'example.com', role: '' },
        { id: 2, slNo: 2, ipAddress: '192.168.1.2', hostname: 'example.net', role: '' },
        // Add more data as needed
    ];

    const handleCheckboxChange = (event, row) => {
        const isChecked = event.target.checked;
        if (isChecked) {
            setSelectedRows([...selectedRows, row]);
        } else {
            setSelectedRows(selectedRows.filter(selectedRow => selectedRow.id !== row.id));
        }
    };

    const handleRoleChange = (event, row) => {
        const updatedData = data.map(item => {
            if (item.id === row.id) {
                return { ...item, role: event.target.value };
            }
            return item;
        });
        console.log('Role updated:', updatedData);
    };

    const handleDeploy = () => {
        console.log('Deploying:', selectedRows);
        // Implement actual deployment logic here, e.g., call an API
    };

    return (
        <div className="data-table-container">
            <h1>Designated Nodes</h1>
            <div className="container">
                <div className="data-table-container">
                    <table className="data-table">
                        <thead>
                            <tr>
                                <th>Sl No.</th>
                                <th>IP Address</th>
                                <th>Hostname</th>
                                <th>Roles</th>
                            </tr>
                        </thead>
                        <tbody>
                            {data.map((row, index) => (
                                <tr key={index}>
                                    <td>{row.slNo}</td>
                                    <td>{row.ipAddress}</td>
                                    <td>{row.hostname}</td>
                                    <td>
                                        <select
                                            value={row.role}
                                            onChange={(event) => handleRoleChange(event, row)}
                                        >
                                            <option value="Admin">Control</option>
                                            <option value="User">Compute</option>
                                            <option value="Guest">Storage</option>
                                        </select>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                <Sidebar />
                <Footer />

                <button
                    className="button-deploy"
                    onClick={handleDeploy}
                    disabled={selectedRows.length === 0}
                >
                    Deploy
                </button>
            </div>
        </div>
    );
};

export default DataTable;
