import React from 'react';
import Sidebar from '../Components/sidebar';
import Footer from '../Components/footer';

import '../Styles/Datatable.css'; // Import your CSS file for styling

const DataTable = () => {
    // Example data, replace with actual data fetching logic
    const data = [
        { slNo: 1, ipAddress: '192.168.1.1', hostname: 'example.com' },
        { slNo: 2, ipAddress: '192.168.1.2', hostname: 'example.net' },
        // Add more data as needed
    ];

    const handleValidate = (rowData) => {
        // Handle validation logic here, e.g., call an API to validate
        console.log('Validating:', rowData);
        // Update state or perform further actions based on validation
    };

    return (
        <div className="data-table-container">
            <h2>Data Table</h2>
            <table className="data-table">
                <thead>
                    <tr>
                        <th>Sl No.</th>
                        <th>IP Address</th>
                        <th>Hostname</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, index) => (
                        <tr key={index}>
                            <td>{row.slNo}</td>
                            <td>{row.ipAddress}</td>
                            <td>{row.hostname}</td>
                            <td>
                                <button onClick={() => handleValidate(row)}>Validate</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default DataTable;
