import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from 'axios'; // Import axios for API calls
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft, faArrowsRotate } from "@fortawesome/free-solid-svg-icons";
import Sidebar from "../Components/sidebar";
import styles from "../Styles/Scaleup.module.css"; // Import CSS Modules

const Scaleup = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Ensure location.state is not null
  const initialSelectedNodes = location.state?.selectedNodes || [];

  const [selectedRows, setSelectedRows] = useState(initialSelectedNodes);
  const [roles, setRoles] = useState(
    initialSelectedNodes.map((node, index) => ({
      id: index + 1,
      slNo: index + 1,
      ipAddress: node.ip,
      hostname: node.hostname,
      roles: [],
    }))
  );

  const [nodes, setNodes] = useState([]);
  const [validationResults, setValidationResults] = useState({});
  const [validatingNode, setValidatingNode] = useState(null);
  const [isScanning, setIsScanning] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 3; // Number of rows per page
  const [isRotating, setIsRotating] = useState(false);

  useEffect(() => {
    scanNetwork();
  }, []);

  useEffect(() => {
    setRoles(selectedRows.map((node, index) => ({
      id: index + 1,
      slNo: index + 1,
      ipAddress: node.ip,
      hostname: node.hostname,
      roles: [],
    })));
  }, [selectedRows]);

  const scanNetwork = async () => {
    setIsScanning(true);
    try {
      const response = await axios.get('http://127.0.0.1:8000/scan');
      setNodes(response.data);
      setValidationResults({});
    } catch (error) {
      console.error('Error scanning network:', error);
    } finally {
      setIsScanning(false);
    }
  };

  const validateNode = async (node) => {
    setValidatingNode(node);
    try {
      const response = await axios.post('http://127.0.0.1:8000/validate', { ip: node.ip });
      setValidationResults(prevResults => ({
        ...prevResults,
        [node.ip]: response.data
      }));
    } catch (error) {
      console.error('Error validating node:', error);
      setValidationResults(prevResults => ({
        ...prevResults,
        [node.ip]: { status: 'failure', message: 'Validation failed due to an error.' }
      }));
    } finally {
      setValidatingNode(null);
    }
  };

  const handleCheckboxChange = (event, row, role) => {
    const isChecked = event.target.checked;
    const updatedRoles = roles.map((item) => {
      if (item.id === row.id) {
        const rolesSet = new Set(item.roles);
        if (isChecked) {
          rolesSet.add(role);
        } else {
          rolesSet.delete(role);
        }
        return { ...item, roles: Array.from(rolesSet) };
      }
      return item;
    });
    setRoles(updatedRoles);

    const updatedSelectedRows = isChecked
      ? [...selectedRows, row]
      : selectedRows.filter((selectedRow) => selectedRow.id !== row.id);
    setSelectedRows(updatedSelectedRows);
  };

  const handleDeploy = () => {
    navigate('/DesignatedNode', { state: { selectedNodes: selectedRows } });
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const handlePreviousPage = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  const handleBack = () => {
    navigate(-1); // Navigate to the previous page in history
    setTimeout(() => {
      window.scrollTo({ top: 0, behavior: "smooth" });
    }, 100); // Delay to ensure navigation completes before scrolling
  };

  const indexOfLastRow = currentPage * rowsPerPage;
  const indexOfFirstRow = indexOfLastRow - rowsPerPage;
  const currentRows = roles.slice(indexOfFirstRow, indexOfLastRow);

  const paginatedNodes = nodes.slice((currentPage - 1) * rowsPerPage, currentPage * rowsPerPage);

  return (
    <div>
      <div className={styles.headers1}>
        <button className={styles.backbutton} onClick={handleBack}>
          <FontAwesomeIcon icon={faArrowLeft} size="2x" />
        </button>
        <center>
          <h1>
            Scale Up
            <button className={`button ${isRotating ? 'rotating' : ''}`} onClick={() => {
              scanNetwork();
              setIsRotating(true);
              setTimeout(() => setIsRotating(false), 1000);
            }}>
              <FontAwesomeIcon icon={faArrowsRotate} size="2x" />
            </button>
          </h1>
        </center>
      </div>
      <div className={styles.container}>
        <table className={styles["data-table"]}>
          <thead>
            <tr>
              <th>Sl No.</th>
              <th>IP Address</th>
              <th>Hostname</th>
              <th>Roles</th>
            </tr>
          </thead>
          <tbody>
            {currentRows.map((row, index) => (
              <tr key={index}>
                <td>{row.slNo}</td>
                <td>{row.ipAddress}</td>
                <td>{row.hostname}</td>
                <td>
                  <div className={styles["checkbox-column"]}>
                    <label>
                      <input
                        type="checkbox"
                        checked={row.roles.includes("HCI")}
                        onChange={(event) =>
                          handleCheckboxChange(event, row, "HCI")
                        }
                      />
                      <span>HCI</span>
                    </label>
                    <br />
                    <label>
                      <input
                        type="checkbox"
                        checked={row.roles.includes("Compute&Storage")}
                        onChange={(event) =>
                          handleCheckboxChange(event, row, "Compute&Storage")
                        }
                      />
                      <span>Compute&Storage</span>
                    </label>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className={styles['pagination-button']}>
          <button onClick={handlePreviousPage} disabled={currentPage === 1}>
            Previous
          </button>
          <button
            onClick={handleNextPage}
            disabled={indexOfLastRow >= roles.length}
          >
            Next
          </button>
        </div>
        <button
          className={styles['deploy-button']}
          onClick={handleDeploy}
          disabled={selectedRows.length === 0}
        >
          <strong>Deploy</strong>
        </button>
        <Sidebar />
      </div>
    </div>
  );
};

export default Scaleup;
