import React from 'react';

const DataTable = () => {
    // Example data, replace with actual data fetching logic
    const data = [
        { slNo: 1, ipAddress: '192.168.1.1', hostname: 'example.com', designation: 'Engineer' },
        { slNo: 2, ipAddress: '192.168.1.2', hostname: 'example.net', designation: 'Manager' },
        // Add more data as needed
    ];

    const handleValidate = (rowIndex) => {
        // Handle validation logic here, e.g., compare designation with validation
        const rowData = data[rowIndex];
        if (rowData.designation === 'validate') {
            alert(`Validation successful for row ${rowIndex + 1}`);
            // Perform further actions based on validation
        } else {
            alert(`Validation failed for row ${rowIndex + 1}`);
        }
    };

    return (
        <div>
            <h2>Data Table</h2>
            <table>
                <thead>
                    <tr>
                        <th>Sl No.</th>
                        <th>IP Address</th>
                        <th>Hostname</th>
                        <th>Designation</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, index) => (
                        <tr key={index}>
                            <td>{row.slNo}</td>
                            <td>{row.ipAddress}</td>
                            <td>{row.hostname}</td>
                            <td>{row.designation}</td>
                            <td>
                                <button onClick={() => handleValidate(index)}>Validate</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default DataTable;
